import React, { useEffect, useState } from "react";

const List = () => {
  const [data, setData] = useState();
  const [error, setError] = useState("");

  async function getData() {
    const response = await fetch("http://localhost:5000");

    const result = await response.json();

    if (!response.ok) {
      console.log(result.error);
      setError(result.error);
    }
    if (response.ok) {
      setData(result);
    }
  }
  useEffect(() => {
    getData();
  }, []);
  console.log(data);

  return (
    <div className="container">
      <h2 className="text-centre">Show all data</h2>
      <div className="row">
        {data.map((ele) => (
          <div key ={ ele._id} className="column">
            <table className="table">
              <thead>
                <tr>
                  <th scope="col">#</th>
                  <th scope="col">{ele.task}</th>
                  <th scope="col">{ele.description}</th>
                  <th scope="col">{ele.deadline}</th>
                  <th scope="col">{ele.status}</th>
                </tr>
              </thead>
            </table>
          </div>
        ))}
      </div>
    </div>
  );
};

export default List;
