import React, {useState} from 'react';

const AddTask = () => {
  const [Task, setTask] = useState("");
  const [Description, setDescription] = useState("");
  const [Deadline, setDeadline] = useState("");
  const [Status, setStatus] = useState("");
  return (
    <div classNameName='container'>
      <h2 classNameName='text-center'>Enter the data</h2>
      <form>
  <div className="mb-3">
    <label className="form-label">Task</label>
    <input type="text" className="form-control" value={Task} onChange={(e) => setTask(e.target.value)} />
  </div>
  <div className="mb-3">
    <label className="form-label">Description</label>
    <input type="text" className="form-control" value={Description} onChange={(e) => setDescription(e.target.value)}/>
  </div>
  <div className="mb-3">
    <label className="form-label">Deadline</label>
    <input type="date" className="form-control" value={Deadline} onChange={(e) => setDeadline(e.target.value)}/>
  </div>
  <div id="status">
  <p>Status:&nbsp;&nbsp;&nbsp;&nbsp;
            <button id="completedBtn" className="btn btn-primary">Mark as Completed</button>
            &nbsp;&nbsp;&nbsp;&nbsp;
            <button id="notCompletedBtn" className="btn btn-primary">Mark as Not Completed</button>
          </p>
</div>

  <button type="submit" className="btn btn-primary">Submit</button>
</form>
    </div>
  )
}

export default AddTask
