import React, {useState} from 'react';

const Addtask = () => {
  const [task, setTask] = useState("");
  const [description, setDescription] = useState("");
  const [deadline, setDeadline] = useState("");
  
  const [error, setError] = useState("");

  console.log(task, description, deadline);

  const handleSubmit = async (e)=> {
      e.preventDefault();
      const addUser = {task, description, deadline}
      const response = await fetch("http://localhost:5000/", {
        method : "POST",
        headers:{
          body: JSON.stringify(addUser),
          "Content-Type": "application/json",
        },

      });
      const result = await response.json();

      if(!response.ok) {
        console.log(result.error);
        setError(result.error);
      }
      if(response.ok) {
        console.log(result);
        setError("");
        setTask("");
        setDescription("");
        setDeadline("");
      }
    };


  return (
    <div className='container'>

      {error && <div class="alert alert-danger">{error}</div>}
      <h2 className='text-center'>Enter the data</h2>
      <form onSubmit={handleSubmit}>
  <div className="mb-3">
    <label className="form-label">task</label>
    <input type="text" className="form-control" value={task} onChange={(e) => setTask(e.target.value)} />
  </div>
  <div className="mb-3">
    <label className="form-label">description</label>
    <input type="text" className="form-control" value={description} onChange={(e) => setDescription(e.target.value)}/>
  </div>
  <div className="mb-3">
    <label className="form-label">deadline</label>
    <input type="date" className="form-control" value={deadline} onChange={(e) => setDeadline(e.target.value)}/>
  </div>
  {/* <div id="status">
  <p>Status:&nbsp;&nbsp;&nbsp;&nbsp;
            <button id="completedBtn" className="btn btn-primary">Mark as Completed</button>
            &nbsp;&nbsp;&nbsp;&nbsp;
            <button id="notCompletedBtn" className="btn btn-primary">Mark as Not Completed</button>
          </p>
</div> */}

  <button type="submit" className="btn btn-primary">Submit</button>
</form>
    </div>
  )
}

export default Addtask;
