import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Update = () => {
  const [data, setData] = useState([]);
  const [error, setError] = useState("");

  async function getData() {
    try {
      const response = await fetch("http://localhost:5000");
      const result = await response.json();

      if (!response.ok) {
        console.log(result.error);
        setError(result.error);
      } else {
        setData(result);
      }
    } catch (error) {
      console.error("Error fetching data:", error);
      setError("Failed to fetch data");
    }
  }

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/${id}`, {
        method: "DELETE"
      });
  
      const result = await response.json();
      if (!response.ok) {
        console.log(result.error);
        setError(result.error);
      } else {
        setError("Data removed successfully");

        setTimeout(() => {
          setError("");
          getData();
          
        }, 1000);

      }
    } catch (error) {
      console.error("Error deleting data:", error);
      setError("Failed to delete data");
    }
  };
  

  useEffect(() => {
    getData();
  }, []);

  console.log(data);

  return (
    <div className="container">
      {error && <div class="alert alert-danger">{error}</div>}
      <h2 className="text-centre">Show all data</h2>
      <div className="row">
        {data && data.length > 0 ? 
        (data?.map((ele) => (
            <div key={ele._id} className="column">
              <table className="table">
                <thead>
                  <tr>
                    <th scope="col">#</th>
                    <th scope="col">{ele.task}</th>
                    <th scope="col">{ele.description}</th>
                    <th scope="col">{ele.deadline}</th>
                    <th scope="col">{ele.status}</th>
                    <Link to={`/${ele._id}`} className="task-delete" onClick={() => handleDelete(ele._id)}>Delete</Link>
                    <Link to={`/${ele._id}`} className="task-edit" onClick={() => handleEdit(ele._id)}>Edit</Link>
                  </tr>
                </thead>
              </table>
            </div>
          ))
        ) : (
          <p>Loading data...</p>
        )}
      </div>
    </div>
  );
};

export default Update;
