import React, { useEffect, useState } from "react";

const List = () => {
  const [data, setData] = useState([]);
  const [error, setError] = useState("");

  async function getData() {
    try {
      const response = await fetch("http://localhost:5000");
      const result = await response.json();

      if (!response.ok) {
        console.log(result.error);
        setError(result.error);
      } else {
        setData(result);
      }
    } catch (error) {
      console.error("Error fetching data:", error);
      setError("Failed to fetch data");
    }
  }

  useEffect(() => {
    getData();
  }, []);

  console.log(data);

  return (
    <div className="container">
      <h2 className="text-centre">Show all data</h2>
      <div className="row">
        {/* Check if data is defined and has elements before mapping */}
        {data && data.length > 0 ? (
          data.map((ele) => (
            <div key={ele._id} className="column">
              <table className="table">
                <thead>
                  <tr>
                    <th scope="col">#</th>
                    <th scope="col">{ele.task}</th>
                    <th scope="col">{ele.description}</th>
                    <th scope="col">{ele.deadline}</th>
                    <th scope="col">{ele.status}</th>
                  </tr>
                </thead>
              </table>
            </div>
          ))
        ) : (
          // Show a message or loader when data is being fetched
          <p>Loading data...</p>
        )}
      </div>
    </div>
  );
};

export default List;
