import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom';

const Addtask = () => {
  const [task, setTask] = useState("");
  const [description, setDescription] = useState("");
  const [deadline, setDeadline] = useState("");
  const [status, setStatus] = useState("pending");

  const statusOptions = [
    { value: "pending", label: "Pending" },
    { value: "completed", label: "Completed" },
    { value: "not_completed", label: "Not Completed" },
  ];
    
  const [error, setError] = useState("");

  const navigate = useNavigate();
  console.log(task, description, deadline);

  const handleSubmit = async (e)=> {
      e.preventDefault();
      const addUser = {task, description, deadline, status}
      const response = await fetch(`http://localhost:5000/`, {
        method : "POST",
        headers:{
          "Content-Type": "application/json",
        },
        body: JSON.stringify(addUser),

      });
      const result = await response.json();

      if(!response.ok) {
        console.log(result.error);
        setError(result.error);
      }
      if(response.ok) {
        console.log(result);
        setError("");
        setTask("");
        setDescription("");
        setDeadline("");
        navigate("/list");
      }
    };


  return (
    <div className='container'>

      {error && <div class="alert alert-danger">{error}</div>}
      <h2 className='text-center'>Enter the data</h2>
      <form onSubmit={handleSubmit}>
  <div className="mb-3">
    <label className="form-label">task</label>
    <input type="text" className="form-control" value={task} onChange={(e) => setTask(e.target.value)} />
  </div>
  <div className="mb-3">
    <label className="form-label">description</label>
    <input type="text" className="form-control" value={description} onChange={(e) => setDescription(e.target.value)}/>
  </div>
  <div className="mb-3">
    <label className="form-label">deadline</label>
    <input type="date" className="form-control" value={deadline} onChange={(e) => setDeadline(e.target.value)}/>
  </div>
  <div className="mb-3">
  <label className="form-label">Status</label>
  <select
    className="form-select"
    value={status}
    onChange={(e) => setStatus(e.target.value)}
  >
    {statusOptions.map((option) => (
      <option key={option.value} value={option.value}>
        {option.label}
      </option>
    ))}
  </select>
</div>


  <button type="submit" className="btn btn-primary">Submit</button>
</form>
    </div>
  )
}

export default Addtask;
